import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const Update = () => {
  const { id } = useParams();
  console.log(id);
  const [updateData, setUpdateData] = useState({});
  const { users, loading } = useSelector((state) => state.app.users);
  useEffect(() => {
    if (id) {
      const user = users.filter((user) => user.id === id);
      setUpdateData(user);
    }
  }, [id, users]);
  console.log(updateData);
  return (
    <div>
      <h2 className="my-2 text-center">Edit the data</h2>
      <form className="w-50 mx-auto my-5">
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">
            Name
          </label>
          <input
            type="text"
            class="form-control"
            name="name"
            value={updateData[0].name}
            // onChange={getUserData}
          />
        </div>
        <div class="mb-3">
          <label for="exampleInputEmail1" class="form-label">
            Email address
          </label>
          <input
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            name="email"
            value={updateData[0].email}
            // onChange={getUserData}
          />
        </div>
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            Age
          </label>
          <input
            type="text"
            class="form-control"
            name="age"
            value={updateData[0].age}
            // onChange={getUserData}
          />
        </div>
        <div class="mb-3">
          <input
            class="form-check-input"
            type="radio"
            id="flexRadioDefault1"
            name="gender"
            value="male"
            checked={updateData[0].gender === "male"}
            // onChange={getUserData}
          />
          <label class="form-check-label">Male</label>
        </div>
        <div class="mb-3">
          <input
            class="form-check-input"
            type="radio"
            name="gender"
            value="female"
            // onChange={getUserData}
            checked={updateData[0].gender === "female"}
          />
          <label class="form-check-label">Female</label>
        </div>
        <button type="submit" class="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
};

export default Update;
